import React, { useState } from 'react';
import './App.css';
import 'h8k-components';

import Articles from './components/Articles';

const title = "Sorting Articles";

function App({articles}) {
    const [articlesList, setArticlesList] = useState(articles);

    const sortByUpVotes = () => {
        var newArticlesList = [];
        Object.assign(newArticlesList, articlesList);
        newArticlesList.sort((a, b) => {
            if(a.upvotes > b.upvotes) {
                return -1;
            }
            if(a.upvotes < b.upvotes) {
                return 1;
            }
            return 0;
        })
        setArticlesList(newArticlesList);
    }

    const sortByDates = () => {
        var newArticlesList = [];
        Object.assign(newArticlesList, articlesList);
        newArticlesList.sort((a, b) => {
            const aDate = new Date(a.date);
            const bDate = new Date(b.date);
            if (aDate > bDate){
                return -1
            }
            if (aDate < bDate){
                return 1
            }
            return 0;
        })
    }

    return (
        <div className="App">
            <h8k-navbar header={title}></h8k-navbar>
            <div className="layout-row align-items-center justify-content-center my-20 navigation">
                <label className="form-hint mb-0 text-uppercase font-weight-light">Sort By</label>
                <button data-testid="most-upvoted-link" className="small" onClick={() => sortByUpVotes()}>Most Upvoted</button>
                <button data-testid="most-recent-link" className="small">Most Recent</button>
            </div>
            <Articles articles={articlesList}/>
        </div>
    );

}

export default App;
